import { Component, OnInit } from '@angular/core';
import {ActivatedRoute, Router} from "@angular/router";
import {OrderService} from "../../../services/shopping/order/order.service";
import {OrderInfo} from "../../../common/users/orderInfo";
import {OrderItem} from "../../../common/users/order-item";

@Component({
  selector: 'app-order-page',
  templateUrl: './order-page.component.html',
  styleUrls: ['./order-page.component.sass']
})
export class OrderPageComponent implements OnInit {

  orderId: number = 0;
  order: OrderInfo = new OrderInfo();
  orderItems: OrderItem[] = [];

  constructor(private route: ActivatedRoute, private orderService: OrderService) { }

  async ngOnInit(): Promise<void> {
    await this.getOrderData();
    await this.retrieveOrderItems();
    await Promise.all([this.getOrderData(), this.retrieveOrderItems()])
  }

  async getOrderData(): Promise<void> {
    this.orderId = +this.route.snapshot.paramMap.get('id')!;
    this.orderService.getOrderById(this.orderId).toPromise().then(
      data => {
        if (data != undefined) {
          this.order = data;
        }
      },
      error => {
        console.log(error);
      }
    )
  }

  async retrieveOrderItems(): Promise<void> {
    await this.getOrderData();

    this.orderService.retrieveOrderItems(this.orderId).subscribe(
      data => {
        this.orderItems = data;
      },
      error => {
        console.log(error);
      }
    )
  }

}
<div class="wrapper">
  <h1>Order Details</h1>
  <div class="order-info">
    <div class="order-info-item">
      <span class="label">Date:</span>
      <span class="value">{{ order.purchaseDate }}</span>
    </div>
    <div class="order-info-item">
      <span class="label">Status:</span>
      <span class="value">{{ order.pending }}</span>
    </div>
    <div class="order-info-item">
      <span class="label">Payment Method:</span>
      <img class="o-paym-icon" [src]="order.paymentMethod.iconURL" alt="Icon Pauy Meth">
    </div>
    <div class="order-info-item">
      <span class="label">Total Price:</span>
      <span class="value">{{ order.total.toFixed(2) + " €"}}</span>
    </div>
  </div>
  <h2>Order Items</h2>
  <table class="order-items-table">
    <thead>
    <tr>
      <th>Image</th>
      <th>Product</th>
      <th>Price</th>
      <th>Quantity</th>
      <th>Size</th>
    </tr>
    </thead>
    <tbody>
    <tr *ngFor="let item of orderItems">
      <td>
        <img class="o-product-img" [src]="item.product.imageURL" alt="">
      </td>
      <td>{{ item.product.name}}</td>
      <td>{{ item.purchasedPrice + " €"}}</td>
      <td>{{ item.quantity }}</td>
      <td>{{ item.relatedStockAndSize.size }}</td>
    </tr>
    </tbody>
  </table>
</div>
