import {Component, OnInit} from '@angular/core';
import {ProductInfo} from "../../../../common/shopping/product-info";
import {ProductService} from "../../../../services/product.service";
import {ActivatedRoute} from "@angular/router";
import {CartItem} from "../../../../common/shopping/cart-item";
import {CartService} from "../../../../services/shopping/cart/cart.service";
import {Stock} from "../../../../common/prod-details/stock";
import {StockService} from "../../../../services/prod-details/stock.service";

@Component({
  selector: 'app-product-page',
  templateUrl: './product-page.component.html',
  styleUrls: ['./product-page.component.sass']
})
export class ProductPageComponent implements OnInit {

  productInfo: ProductInfo = new ProductInfo();
  cartItem!: CartItem;
  currentProductId: number = 1;
  stock!: Stock[];


  constructor(private route: ActivatedRoute,
              private productService: ProductService,
              private stockService: StockService,
              private cartService: CartService) {
  }

  async ngOnInit() {
    this.cartItem = new CartItem();
    this.route.paramMap.subscribe(() => {
      this.getProductById();
      this.getAStock();
    })
  }

  async getAStock() {
    this.stockService.findStockByProduct(this.productInfo.productId).subscribe(data => {
      this.stock = data;
    })
  }

  async getProductById() {
    this.currentProductId = +this.route.snapshot.paramMap.get('prodId')!;

    this.productService.getProductById(this.currentProductId).subscribe(
      data => {
        this.productInfo = data;
        this.stock
      }
    );
  }


  addToCart() {
    this.cartItem.productInfo = this.productInfo;
    this.cartService.addToCart(this.cartItem).subscribe()
  }

  increment() {
    this.cartItem.quantity++;
  }

  decrement() {
    this.cartItem.quantity--;
    if (this.cartItem.quantity == 0)
      this.cartItem.quantity = 1;
  }
}
<div class="product-page">
  <div class="details-container">
    <h1 class="product-name">{{productInfo.name}}</h1>
    <p class="product-price">
      200 €
    </p>
    <hr class="horizontal-line">
    <p class="product-size">size</p>
    <div class="product-buttons">
      <div class="quantity-buttons">
        <button class="round-button" (click)="increment()">+</button>
        <p>{{cartItem.quantity}}</p>
        <button class="round-button" (click)="decrement()">-</button>
      </div>
      <div class="size-buttons">
        <button class="product-btn">Small</button>
        <button class="product-btn">Medium</button>
        <button class="product-btn">Large</button>
        <button class="product-btn">X-Large</button>
      </div>

    </div>
    <button class="add-to-cart-btn" (click)="addToCart()"><span>Add to cart</span></button>
    <p class="product-description">Lorem ipsum dolor sit amet, consectetur adipisicing elit. Accusamus ad adipisci
      aliquid atque autem blanditiis consequuntur delectus eius explicabo impedit inventore ipsa, maiores molestiae,
      mollitia numquam optio perspiciatis, tempora ut.</p>
  </div>
  <div class="image-container">
    <img [src]="productInfo.imageURL" class="product-image">
  </div>
</div>
