import {Component, OnInit} from '@angular/core';
import {Cart} from "../../../../common/shopping/cart";
import {CartService} from "../../../../services/shopping/cart/cart.service";
import {CartItem} from "../../../../common/shopping/cart-item";
import {TokenStorageService} from "../../../../services/authentication/token-storage.service";
import {CategoryService} from "../../../../services/prod-details/category/category.service";

@Component({
  selector: 'app-cart-page',
  templateUrl: './cart-page.component.html',
  styleUrls: ['./cart-page.component.sass']
})
export class CartPageComponent implements OnInit {

  cart: Cart = new Cart();
  cartItems: CartItem[] = [];

  constructor(private cartService: CartService,
              private tokenStorageService: TokenStorageService,
              private categoryService: CategoryService) {
  }

  async ngOnInit(){
    await this.retrieveUserCart();
    await this.retrieveCartItems(this.cart);
  }

  async retrieveCartItems(cart: Cart){
    await this.cartService.retrieveCartItems(cart).subscribe(
      data => {
        this.cartItems = data;
      },
      error => {
        console.log(error);
      }
    );
  }

  async retrieveUserCart() {
    await this.cartService.retrieveCart().subscribe(
      data => {
        this.cart = data;
      },
      error => {
        console.log(error);
      }
    );
  }
}
<h1>Your Shopping Cart</h1>
<div class="cart-container">
  <table class="cart-table">
    <tr>
      <th>
        <p>Product</p>
      </th>
      <th>Quantity</th>
      <th>Total</th>
    </tr>
    <tr *ngFor="let item of cartItems">
      <td>
        <div class="cart-prod-details">
          <img [src]="item.productInfo.imageURL" alt="">
          <div class="cart-texts">
            <p>{{item.productInfo.name}}</p>
            <small>{{item.productInfo.price}}</small>
            <br>
            <a href="">Remove</a>
          </div>
        </div>
      </td>
      <td>{{item.totalPrice}}</td>
      <td>
        <button class="quantity-button increment-button">-</button>
        <span>{{item.quantity}}</span>
        <button class="quantity-button decrement-button">+</button>
      </td>
    </tr>
    <tr class="horizontal-line">
      <td></td>
    </tr>
    <div class="subtotal-details">
      <h2>Subtotal</h2>
      <h3>200€</h3>
      <h2>Delivery fees</h2>
      <h3>3.5€</h3>
      <button><a [routerLink]="['/checkout']">Checkout</a></button>
    </div>
  </table>
</div>
