import { Component, OnInit } from '@angular/core';
import { Cart } from "../../../../common/shopping/cart";
import { CartService } from "../../../../services/shopping/cart/cart.service";
import { CartItem } from "../../../../common/shopping/cart-item";
import { TokenStorageService } from "../../../../services/authentication/token-storage.service";
import { Observable } from "rxjs";
import { CategoryService } from "../../../../services/prod-details/category.service";
import { Category } from "../../../../common/prod-details/category";
import {error} from "@angular/compiler-cli/src/transformers/util";

@Component({
  selector: 'app-cart-page',
  templateUrl: './cart-page.component.html',
  styleUrls: ['./cart-page.component.sass']
})
export class CartPageComponent implements OnInit {

  cart: Cart = new Cart();
  cartItems: CartItem[] = [];

  constructor(
    private cartService: CartService,
    private tokenStorageService: TokenStorageService,
    private categoryService: CategoryService
  ) { }

  async ngOnInit(): Promise<void> {
    await this.retrieveUserCart();
    await this.retrieveCartItems(this.cart);
  }

  async retrieveCartItems(cart: Cart): Promise<void> {
    await this.cartService.retrieveCartItems(cart).subscribe(
      data => {
        this.cartItems = data;
      },
      error =>  {
        console.log(error);
      }
    );
  }

  async retrieveUserCart(): Promise<void> {
    await this.cartService.retrieveCart().subscribe(
      data => {
        this.cart = data;
      },
      error => {
        console.log(error);
      }
    );
  }
}
